(function(){"use strict";function aos_init(){AOS.init({duration:1e3,easing:"ease-in-out",once:true,mirror:false})}const select=(el,all=false)=>{el=el.trim();if(all){return[...document.querySelectorAll(el)]}else{return document.querySelector(el)}};const on=(type,el,listener,all=false)=>{if(all){select(el,all).forEach(e=>e.addEventListener(type,listener))}else{select(el,all).addEventListener(type,listener)}};const onscroll=(el,listener)=>{el.addEventListener("scroll",listener)};let navbarlinks=select("#navbar .scrollto",true);const navbarlinksActive=()=>{let position=window.scrollY+200;navbarlinks.forEach(navbarlink=>{if(!navbarlink.hash)return;let section=select(navbarlink.hash);if(!section)return;if(position>=section.offsetTop&&position<=section.offsetTop+section.offsetHeight){navbarlink.classList.add("active")}else{navbarlink.classList.remove("active")}})};window.addEventListener("load",()=>{navbarlinksActive;aos_init()});onscroll(document,navbarlinksActive);const scrollto=el=>{let header=select("#header");let offset=header.offsetHeight;if(!header.classList.contains("header-scrolled")){offset-=10}let elementPos=select(el).offsetTop;window.scrollTo({top:elementPos-offset,behavior:"smooth"})};let selectHeader=select("#header");if(selectHeader){const headerScrolled=()=>{if(window.scrollY>56){selectHeader.classList.add("header-scrolled")}else{selectHeader.classList.remove("header-scrolled")}};window.addEventListener("load",headerScrolled);onscroll(document,headerScrolled)}on("click",".mobile-nav-toggle",function(e){select(".mobile-menu").classList.toggle("mobile-menu-visible")});on("click",".mobile-menu-close-btn",function(e){select(".mobile-menu").classList.remove("mobile-menu-visible")});on("click",".menu-backdrop",function(e){select(".mobile-menu").classList.remove("mobile-menu-visible")});on("click",".scrollto",function(e){if(select(this.hash)){e.preventDefault();let mobile_menu=select(".mobile-menu");if(mobile_menu.classList.contains("mobile-menu-visible")){mobile_menu.classList.remove("mobile-menu-visible")}scrollto(this.hash)}},true)})();